const CACHE_NAME = 'yaygara-v1.4';
const ASSETS_TO_CACHE = [
  '/',
  '/index.html',
  '/manifest.json',
  '/locales/tr.json',
  '/locales/en.json',
  '/decks/baslangic.tr.json',
  '/decks/zihin-acici.tr.json',
  '/decks/karanlik-seruven.tr.json',
  '/decks/uygarligin-izleri.tr.json',
  '/decks/argo.tr.json',
];
// Pre-cache assets that are generated by the build process
// We can't know the exact filenames, so we'll use a placeholder
// that the build process should replace. For this simulation,
// we'll assume a common pattern.
const DYNAMIC_ASSETS_PATTERN = /\.(js|css|svg|woff2)$/;
self.addEventListener('install', (event) => {
  event.waitUntil(
    caches.open(CACHE_NAME).then((cache) => {
      console.log('Opened cache and caching static assets');
      // We can't know the build output filenames, so we cache the static JSONs and main pages.
      // The fetch handler will cache dynamic assets on the fly.
      return cache.addAll(ASSETS_TO_CACHE);
    })
  );
  self.skipWaiting();
});
self.addEventListener('activate', (event) => {
  event.waitUntil(
    caches.keys().then((cacheNames) => {
      return Promise.all(
        cacheNames.map((cacheName) => {
          if (cacheName !== CACHE_NAME) {
            console.log('Deleting old cache:', cacheName);
            return caches.delete(cacheName);
          }
        })
      );
    })
  );
  self.clients.claim();
});
self.addEventListener('fetch', (event) => {
  const url = new URL(event.request.url);
  const isDeckRequest = url.pathname.includes('/decks/');

  // For navigation requests, use a network-first strategy fall-backing to the root index.html
  if (event.request.mode === 'navigate') {
    event.respondWith(
      fetch(event.request)
        .then((response) => {
          if (response && response.status === 200) {
            const resClone = response.clone();
            caches.open(CACHE_NAME).then((cache) => {
              cache.put(event.request, resClone);
            });
          }
          return response;
        })
        .catch(() => {
          return caches.match('/');
        })
    );
    return;
  }

  // Network First Strategy for everything except decks
  if (!isDeckRequest) {
    event.respondWith(
      fetch(event.request)
        .then((response) => {
          if (response && response.status === 200) {
            const resClone = response.clone();
            caches.open(CACHE_NAME).then((cache) => {
              cache.put(event.request, resClone);
            });
          }
          return response;
        })
        .catch(() => {
          return caches.match(event.request);
        })
    );
    return;
  }

  // Cache First Strategy for deck files
  event.respondWith(
    caches.match(event.request).then((response) => {
      if (response) {
        return response;
      }
      return fetch(event.request).then((networkResponse) => {
        if (networkResponse && networkResponse.status === 200) {
          const resClone = networkResponse.clone();
          caches.open(CACHE_NAME).then((cache) => {
            cache.put(event.request, resClone);
          });
        }
        return networkResponse;
      });
    })
  );
});